_carousel.scss:
// Carousel slide fix
#slick-slide00 > div > div > img,
#slick-slide01 > div > div > img,
#slick-slide02 > div > div > img,
#slick-slide03 > div > div > img {
  margin-left: -2rem !important;
}

#body
  > div.body.content
  > div.container.main
  > div.home.home-page-with-sidenav.slider-in-container
  > div
  > div.main.floated-carousel.carouselFlex
  > div.main-content
  > div.slider.float.in-container
  > section
  > div {
  height: 21rem;
}

#body
  > div.body.content
  > div.container.main
  > div.home.home-page-with-sidenav.slider-in-container
  > div
  > div.main.floated-carousel.carouselFlex
  > div.main-content
  > div.slider.float.in-container {
  position: relative;
}

