//Carousel background change
#body
  > div.body.content
  > div.container.main
  > div.home.home-page-with-sidenav.slider-in-container
  > div
  > div.main.floated-carousel
  > div.main-content
  > div.slider.float.in-container {
  background: white !important;
}

//Client section under Carousel
.clientSection {
  position: relative;
  display: flex;
  height: 15em;
  background: white;
  max-width: 720px;
  h5 {
    font-family: "Roboto", sans-serif !important;
    font-weight: 900;
    font-size: 0.9375rem;
    line-height: 1.875rem;
    background: $primaryRed;
    color: white;
    text-align: center;
    border-bottom: 3px solid #8b8b8b;
  }
  .fullSpecs {
    position: relative;
    width: 50%;
    height: 100%;
    .vertDivide {
      position: relative;
      height: 70%;
      width: 100%;
      display: flex;
      justify-content: center;
      align-items: center;
      border-right: 1px solid rgba(198, 198, 198, 0.5);
      .pdfButton {
        position: relative;
        left: 4.9em;
        width: 60%;
      }
    }
  }
  .pastClients {
    width: 50%;
    .companyGif {
      height: 70%;
      display: flex;
      justify-content: center;
      align-items: center;
      img {
        width: 8em;
      }
    }
  }
}

//Fixes

@include media(">phone", "<lgphone") {
  .pdfButton {
    position: relative;
    left: 0em !important;
    width: 40em !important;
  }
}
