pages/compare:
---
blog:
    recent_posts:
        limit: {{theme_settings.footer_blog_posts_count}}
---
{{#partial "page"}}
{{inject 'comparisons' comparisons}}
{{inject 'compareRemoveMessage' (lang 'compare.no_remove')}}
{{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}
<h1 class="page-heading">{{lang 'compare.header' products=comparisons.length}}</h1>
<div id="compare-page" class="page compare-page category">
    <main class="page-content no-sidenav" id="product-listing-container">
      <!-- GRID VIEW ONLY -->

      <form class="grid grid-only">
                <ul class="productGrid grid-only productGrid--maxCol visible">
                  {{#each comparisons}}
                    <li class="product grid-view">
                      <article id="{{#or customer (if ../theme_settings.restrict_to_login '===' true)}}pricing-restricted{{/or}}" class="card compare-disabled {{#if alternate}}card--alternate{{/if}} price-visibility{{#or customer (if ../theme_settings.restrict_to_login '===' true)}} restricted{{/or}} {{#if ../theme_settings.show_sale_price_label '===' true}}sale-label-active{{/if}} {{#if ../theme_settings.card-brand-name '===' true}}brand-sku-active brand-active{{/if}} {{#if ../theme_settings.card-brand-name '===' false}}brand-hidden{{/if}} {{#if ../theme_settings.card-sku '===' true}}brand-sku-active sku-active{{/if}} {{#if ../theme_settings.card-sku '===' false}}sku-hidden{{/if}} {{#if ../theme_settings.card-brand-sku-separate-lines '===' true}}brand-sku-separate-lines{{/if}} {{#if ../theme_settings.card-brand-2-lines '===' true}}brand-2-lines{{/if}} {{#if ../theme_settings.card-sku-2-lines '===' true}}sku-2-lines{{/if}} {{#if ../theme_settings.msrp-price '===' true}}msrp-visible{{else}}msrp-hidden{{/if}} {{#if ../theme_settings.msrp-before-sale-pricing-separate-lines '===' true}}msrp-and-regular-separate-lines{{/if}} {{#if ../theme_settings.before-sale-price '===' true}} before-sale-price-visible{{else}}before-sale-price-hidden{{/if}} {{#if ../theme_settings.card-title-lines '===' '1-line'}} card-title-one-line{{/if}} {{#if ../theme_settings.card-title-lines '===' '2-lines'}}card-title-two-lines{{/if}} {{#if ../theme_settings.card-title-lines '===' '3-lines'}}card-title-three-lines{{/if}} {{#if ../theme_settings.card-title-lines '===' '4-lines'}}card-title-four-lines{{/if}} {{#if ../theme_settings.current-price-on-two-lines '===' true}}current-price-on-two-lines {{/if}} {{#if ../theme_settings.price_ranges '===' true}}price-ranges-active{{/if}} {{#if ../theme_settings.card-text-align '===' 'center'}} center-align{{/if}} {{#if price.with_tax}} withTax{{/if}}{{#if price.without_tax}} withoutTax{{/if}}" {{#if settings.data_tag_enabled}} data-event-type="{{event}}" data-entity-id="{{id}}" data-position="{{position}}" data-name="{{name}}" data-product-category="{{#each category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}" data-product-brand="{{brand.name}}" data-product-price="{{#if price.with_tax}}{{price.with_tax.value}}{{else}}{{price.without_tax.value}}{{/if}}"  {{/if}}>
                        <figure class="card-figure {{#if ../theme_settings.product_sale_badges_visibility '===' true}} sale-flag-active{{else}} sale-flag-inactive{{/if}}">
                          <a class="image-link" href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>
                              <img class="card-image lazyload" data-sizes="auto" src="{{cdn 'img/loading.svg'}}" data-src="{{getImage image 'product_size' (cdn ../theme_settings.default_image_product)}}" alt="{{image.alt}}" title="{{image.alt}}">
                              {{#if ../theme_settings.product_hover_images '===' 'active'}}
                                {{#each (limit images 2)}}
                                  {{#if @index '>' 0}}
                                    <img class='card-image alt lazyload' src="{{cdn 'img/loading.svg'}}" data-src="{{getImage this 'productgallery_size' (cdn theme_settings.default_image_product)}}" alt="{{this.alt}}" title="{{this.alt}}">
                                  {{/if}}
                                {{/each}}
                              {{/if}}
                            </a>
                            <a id ="remove" class="compareTable-removeProduct" data-comparison-remove href="{{#if remove_url}}{{remove_url}}{{else}}#{{/if}}">
                                <svg class="icon">
                                    <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-close"></use>
                                </svg>
                            </a>
                        </figure>
                        <div class="card-wrapper">
                          <div class="card-body top with-as-low-as-pricing purchase-ability{{#if ../settings.show_product_rating}} ratings-on{{else}} ratings-off{{/if}}{{#if price.with_tax}} withTax{{/if}}{{#if price.without_tax}} withoutTax{{/if}}{{#or customer (if ../theme_settings.restrict_to_login '===' true)}} restricted{{/or}}" data-test-info-type="price">
                            <div class="card-sku-brand-section {{#if ../theme_settings.card-brand-sku-separate-lines '===' true}} separate-lines {{/if}} {{#if ../theme_settings.card-brand-2-lines '===' true}} brand-two-lines {{/if}} {{#if ../theme_settings.card-sku-2-lines '===' true}} sku-two-lines {{/if}}">
                              {{#if ../theme_settings.card-brand-name '===' true}}
                                <h4 class="card-text brand {{#if ../theme_settings.card-brand-2-lines '===' true}} two-lines {{/if}}" data-test-info-type="brandName"><a class="brand-link" href="/brands/{{dashcase brand.name}}">{{brand.name}}</a></h4>
                              {{/if}}
                                 {{#if brand.name}}
                                   {{#if sku}}
                                     <span class="brand-sku-divider">|</span>
                                   {{/if}}
                                 {{/if}}
                              <h4 class="card-text sku {{#if ../theme_settings.card-sku-2-lines '===' true}} two-lines {{/if}}">
                                {{#if ../theme_settings.card-sku '===' true}}
                                {{#if sku}}
                                <a class="sku-link" href="{{url}}" {{#if settings.data_tag_enabled}} data-event-type="product-click" {{/if}}>
                                  <span class="sku-label">{{lang 'sku'}}:</span>
                                  <span class="sku-value" data-product-sku>{{sku}}</span>
                                </a>
                                {{/if}}
                                {{/if}}
                              </h4>
                            </div>
                            <h4 class="card-title">
                                <a href="{{url}}">{{ name }}</a>
                            </h4>

                            {{#if ../settings.show_product_rating}}
                                <p class="card-text" data-test-info-type="productRating">
                                    <span class="rating--small">
                                        {{> components/products/ratings rating=rating}}
                                    </span>
                                </p>
                            {{/if}}

                            <div class="price-visibility{{#or customer (if theme_settings.restrict_to_login '===' true)}} visible{{/or}}">
                              {{#or ../customer (if ../theme_settings.restrict_to_login '!==' true)}}
                                {{#if price_range}}
                                  {{> components/products/price-range}}
                                {{else}}
                                  {{> components/products/price price=price}}
                                {{/if}}
                                {{else}}
                                  {{> components/common/login-for-pricing}}
                              {{/or}}
                            </div>

                            {{#or customer (if ../theme_settings.restrict_to_login '!==' true)}}
                            {{#if show_cart_action}}
                            <div class="card-text add-to-cart-button {{#if stock_level '<' 1}}{{#if out_of_stock_message '===' false}} no-button{{/if}}{{/if}}">
                                    {{#if show_cart_action}}
                                        {{#if has_options}}
                                            <a href="{{url}}" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{lang 'products.choose_options'}}</a>
                                        {{/if}}
                                        {{#if pre_order}}
                                            <a href="{{url}}" class="button button--small card-figcaption-button">{{lang 'products.pre_order'}}</a>
                                        {{/if}}
                                        {{#if add_to_cart_url }}
                                            <a href="{{add_to_cart_url}}" class="button button--small card-figcaption-button">{{lang 'products.add_to_cart'}}</a>
                                        {{/if}}
                                        {{#if out_of_stock_message }}
                                            <a href="{{url}}" class="button button--small card-figcaption-button" data-product-id="{{id}}">{{out_of_stock_message}}</a>
                                        {{/if}}
                                        {{#if stock_level '<' 1}}
                                          {{#if out_of_stock_message '===' false}}
                                            <a href="{{url}}" class="button button--small card-figcaption-button false-stock-message" data-product-id="{{id}}">{{out_of_stock_message}}</a>
                                          {{/if}}
                                        {{/if}}
                                    {{/if}}
                             </div>
                            {{/if}}
                            {{/or}}
                          </div>

                          <div class="card-body bottom">
                            <div class="card-text">
                              <div class="brand">
                                <span class="compareTable-headingText brand">Brand: </span>
                                {{#if brand.name}}
                                  <a href="{{brand.url}}">{{ brand.name }}</a>
                                {{else}}
                                  <span class="normal-text">{{lang 'common.not_applicable'}}</span>
                                {{/if}}
                              </div>
                            <span class="compareTable-headingText">Description</span>
                              <div class="normal-text">{{ summary }}</div>

                              <div class="rating">
                                  <div class="compareTable-heading">
                                    <span class="compareTable-headingText rating">Rating</span>
                                  </div>

                                  <div class="compareTable-item rating">
                                    {{#if ../settings.show_product_rating}}
                                        <p class="card-text" data-test-info-type="productRating">
                                            <span class="rating--small">
                                                {{> components/products/ratings rating=rating}}
                                            </span>
                                        </p>
                                    {{/if}}
                                  </div>

                              <div class="availability">
                                  <span class="compareTable-headingText">Availability</span>
                                  <div class="availability-text normal-text">
                                    {{#if availability}}
                                      {{ availability }}
                                    {{else}}
                                      {{lang 'common.not_applicable'}}
                                    {{/if}}
                                  </div>
                              </div>

                              <span class="compareTable-headingText">{{lang 'common.other_details'}}</span>
                              <div class="custom-fields">
                                  {{#if custom_fields}}
                                      <ul>
                                          {{#each custom_fields}}
                                              <li><span class="name">{{name}}:</span><br /><span class="value">{{{value}}}</span></li>
                                          {{/each}}
                                      </ul>
                                  {{else}}
                                      {{lang 'common.not_applicable'}}
                                  {{/if}}
                               </div> <!-- custom-fields -->

                              </div> <!-- card-text -->

                       </div>   <!-- card-body -->
                    </article>
                    </li>
                {{/each}}
                </ul>
      </form> <!-- grid only -->

    </main>
</div>

{{/partial}}
{{> layout/base}}

components/products/ratings:
{{#for 1 5}}
    {{#if ../this.rating '>=' $index}}
        <span class="icon icon--ratingFull">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{else}}
        <span class="icon icon--ratingEmpty">
            <svg>
                <use xlink:href="#icon-star" />
            </svg>
        </span>
    {{/if}}
{{/for}}
{{{snippet 'product_rating'}}}

components/products/price-range:
{{#and price.retail_price_range.min.with_tax price.retail_price_range.max.with_tax}}
    <div class="price-section price-section--withTax rrp-price--withTax">
        {{theme_settings.pdp-retail-price-label}}
        <span data-product-rrp-price-with-tax class="price price--rrp">{{price.retail_price_range.min.with_tax.formatted}} - {{price.retail_price_range.max.with_tax.formatted}}</span>
    </div>
{{else}}
    {{#if price.with_tax}}
      <div class="msrp-sale-regular-price-section withTax">
        <div class="price-section price-section--withTax rrp-price--withTax" style="display: none;">
            {{theme_settings.pdp-retail-price-label}}
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
      </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withTax non-sale-price non-sale-price--withTax" style="display: none;">
        {{theme_settings.pdp-non-sale-price-label}}
        <span data-product-non-sale-price-with-tax class="price price--non-sale">
            {{price.non_sale_price_with_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withTax current-price price-range{{#if price.non_sale_price_with_tax}} sale-price {{else}} regular-price{{/if}}" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
        <span class="price-label">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label" style="display: none;">{{theme_settings.pdp-sale-price-label}}</span>
        <span data-product-price-with-tax class="price price--withTax">{{price.price_range.min.with_tax.formatted}} - {{price.price_range.max.with_tax.formatted}}</span>
        {{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
            <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}
        {{#if schema_org}}
            <meta itemprop="availability" itemtype="http://schema.org/ItemAvailability" content="http://schema.org/{{#if product.pre_order}}PreOrder{{else if product.can_purchase '===' false}}OutOfStock{{else if product.out_of_stock}}OutOfStock{{else if product.out_of_stock_message}}OutOfStock{{else}}InStock{{/if}}">
            <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                <meta itemprop="minPrice" content="{{price_range.min.with_tax.value}}"  />
                <meta itemprop="price" content="{{price_range.min.with_tax.value}}">
                <meta itemprop="maxPrice" content="{{price_range.max.with_tax.value}}"  />
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="valueAddedTaxIncluded" content="true">
            </div>
        {{/if}}
    </div>
{{/and}}
{{#and price.retail_price_range.min.without_tax price.retail_price_range.max.without_tax}}
    <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}">
        {{theme_settings.pdp-retail-price-label}}
        <span data-product-rrp-price-without-tax class="price price--rrp">{{price.retail_price_range.min.without_tax.formatted}} - {{price.retail_price_range.max.without_tax.formatted}}</span>
    </div>
{{else}}
    {{#if price.without_tax}}
      <div class="msrp-sale-regular-price-section withoutTax">
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" style="display: none;">
            {{theme_settings.pdp-retail-price-label}}
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
      </div>
    {{/if}}
{{/and}}
{{#and price.price_range.min.without_tax price.price_range.max.without_tax}}
    {{!-- Never display the "non-sales price" if there is a price range to be shown, but we do want the element on the page --}}
    <div class="price-section price-section--withoutTax non-sale-price non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" style="display: none;">
        {{theme_settings.pdp-non-sale-price-label}}
        <span data-product-non-sale-price-without-tax class="price price--non-sale">
            {{price.non_sale_price_without_tax.formatted}}
        </span>
    </div>
    <div class="price-section price-section--withoutTax current-price price-range{{#if price.non_sale_price_with_tax}} sale-price {{else}} regular-price{{/if}}{{#and price_range.min.with_tax price_range.max.with_tax}} price-section--minor{{/and}}" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
        <span class="price-label">{{theme_settings.pdp-price-label}}</span>
        <span class="price-now-label" style="display: none;">{{theme_settings.pdp-sale-price-label}}</span>
        <span data-product-price-without-tax class="price price--withoutTax">{{price.price_range.min.without_tax.formatted}} - {{price.price_range.max.without_tax.formatted}}</span>
        {{#and price.price_range.min.with_tax price.price_range.max.with_tax}}
            <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.price_range.min.tax_label}}</abbr>
        {{/and}}
        {{#if schema_org}}
            <meta itemprop="availability" itemtype="http://schema.org/ItemAvailability" content="http://schema.org/{{#if product.pre_order}}PreOrder{{else if product.can_purchase '===' false}}OutOfStock{{else if product.out_of_stock}}OutOfStock{{else if product.out_of_stock_message}}OutOfStock{{else}}InStock{{/if}}">
            <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
            <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                <meta itemprop="minPrice" content="{{price.price_range.min.without_tax.value}}"  />
                <meta itemprop="price" content="{{price.price_range.min.without_tax.value}}">
                <meta itemprop="maxPrice" content="{{price.price_range.max.without_tax.value}}"  />
                <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                <meta itemprop="valueAddedTaxIncluded" content="false">
            </div>
        {{/if}}
    </div>
{{/and}}

{{!-- Never display the "saving price" element by default if there is a price range on the page to be shown --}}
{{#if page_type '===' 'product'}}
     <div class="price-section price-section--saving price" style="display: none;">
            <span class="price">{{lang 'products.you_save_opening_text'}}</span>
            <span data-product-price-saved class="price price--saving">
                {{price.saved.formatted}}
            </span>
            <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
     </div>
{{/if}}

components/products/price:
{{!-- There are 2 code paths for generating the PDP pricing HTML determined by the following #and condition.
If a 'price_range' exists then we generate all HTML in price-range.html, otherwise it is defined here. Both code
paths generate the same HTML structure with some differences in whether that element is displayed by default (css styling).

If you are making a change here or in price-range.html, you probably want to make that change in both files. --}}

{{#and price.price_range (if theme_settings.price_ranges '==' true)}}
    {{> components/products/price-range price=price schema_org=schema_org}}
{{else}}
    {{#if price.with_tax}}
      <div class="msrp-sale-regular-price-section withTax">
        <div class="price-section price-section--withTax rrp-price--withTax" {{#unless price.rrp_with_tax}}style="display: none;"{{/unless}}>
            <span class="price-label">{{theme_settings.pdp-retail-price-label}}</span>
            <span data-product-rrp-with-tax class="price price--rrp">
                {{price.rrp_with_tax.formatted}}
            </span>
        </div>
        <div class="price-section price-section--withTax non-sale-price non-sale-price--withTax" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
            <span class="price-label">{{theme_settings.pdp-non-sale-price-label}}</span>
            <span data-product-non-sale-price-with-tax class="price price--non-sale">
                {{price.non_sale_price_with_tax.formatted}}
            </span>
        </div>
      </div>
        <div class="price-section price-section--withTax current-price{{#if price.non_sale_price_with_tax}} sale-price {{else}} regular-price{{/if}}" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            <span class="price-label" {{#if price.non_sale_price_with_tax}}style="display: none;"{{/if}}>
                {{theme_settings.pdp-price-label}}
            </span>

              <span class="price-label price-now-label" {{#unless price.non_sale_price_with_tax}}style="display: none;"{{/unless}}>
                {{theme_settings.pdp-sale-price-label}}
              </span>

            <span data-product-price-with-tax class="price price--withTax">{{price.with_tax.formatted}}</span>
            {{#if schema_org}}
                <meta itemprop="availability" itemtype="http://schema.org/ItemAvailability" content="http://schema.org/{{#if product.pre_order}}PreOrder{{else if product.can_purchase '===' false}}OutOfStock{{else if product.out_of_stock}}OutOfStock{{else if product.out_of_stock_message}}OutOfStock{{else}}InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
                <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.with_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="true">
                </div>
            {{/if}}
            {{#if price.without_tax}}
                <abbr title="{{lang 'products.including_tax'}}">{{lang 'products.price_with_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}
        </div>
    {{/if}}
    {{#if price.without_tax}}
      <div class="msrp-sale-regular-price-section withoutTax">
        <div class="price-section price-section--withoutTax rrp-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.rrp_without_tax}}style="display: none;"{{/unless}}>
            <span class="price-label">{{theme_settings.pdp-retail-price-label}}</span>
            <span data-product-rrp-price-without-tax class="price price--rrp">
                {{price.rrp_without_tax.formatted}}
            </span>
        </div>
        {{#if price.non_sale_price_without_tax.formatted}}
        <div class="price-section price-section--withoutTax non-sale-price non-sale-price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
            <span class="price-label">{{theme_settings.pdp-non-sale-price-label}}</span>
            <span data-product-non-sale-price-without-tax class="price price--non-sale">
                {{price.non_sale_price_without_tax.formatted}}
            </span>
        </div>
      </div>
        {{/if}}
        </div>
        <div class="price-section price-section--withoutTax current-price{{#if price.non_sale_price_without_tax}} sale-price {{else}} regular-price{{/if}}" {{#if schema_org}}itemprop="offers" itemscope itemtype="http://schema.org/Offer"{{/if}}>
            <span class="price-label" {{#if price.non_sale_price_without_tax}}style="display: none;"{{/if}}>
                {{theme_settings.pdp-price-label}}
            </span>

              <span class="price-label price-now-label" {{#unless price.non_sale_price_without_tax}}style="display: none;"{{/unless}}>
                {{theme_settings.pdp-sale-price-label}}
              </span>

            <span data-product-price-without-tax class="price price--withoutTax{{#if price.with_tax}} price-section--minor{{/if}}">{{price.without_tax.formatted}}</span>
            {{#if schema_org}}
                <meta itemprop="availability" itemtype="http://schema.org/ItemAvailability" content="http://schema.org/{{#if product.pre_order}}PreOrder{{else if product.can_purchase '===' false}}OutOfStock{{else if product.out_of_stock}}OutOfStock{{else if product.out_of_stock_message}}OutOfStock{{else}}InStock{{/if}}">
                <meta itemprop="itemCondition" itemtype="http://schema.org/OfferItemCondition" content="http://schema.org/{{product.condition}}Condition">
                <div itemprop="priceSpecification" itemscope itemtype="http://schema.org/PriceSpecification">
                    <meta itemprop="price" content="{{price.without_tax.value}}">
                    <meta itemprop="priceCurrency" content="{{currency_selector.active_currency_code}}">
                    <meta itemprop="valueAddedTaxIncluded" content="false">
                </div>
            {{/if}}
            {{#if price.with_tax}}
                <abbr title="{{lang 'products.excluding_tax'}}">{{lang 'products.price_without_tax' tax_label=price.tax_label}}</abbr>
            {{/if}}

    {{/if}}
    {{#if page_type '===' 'product'}}
         <div class="price-section price-section--saving price" {{#unless price.saved}}style="display: none;"{{/unless}}>
                <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                <span data-product-price-saved class="price price--saving">
                    {{price.saved.formatted}}
                </span>
                <span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
         </div>
    {{/if}}
{{/and}}

components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<div class="currency-converter">
<li class="navUser-item currency-menu">
    <a class="navUser-action has-dropdown" title="Currency Selector" alt="Currency Selector" href="#" data-dropdown="currencySelection" aria-controls="currencySelection" aria-expanded="false">{{currency_selector.active_currency_code}} <i class="icon currency" aria-hidden="true"><svg><use xlink:href="#icon-chevron-down" /></svg></i></a>
    <ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
        {{#each currency_selector.currencies}}
        <li class="dropdown-menu-item">
            <a class="currency-name-switcher" href="{{{switch_url}}}">
                {{#if is_active}}
                <strong>{{{flag}}} {{name}}</strong>
                {{else}}
                {{{flag}}} {{name}}
                {{/if}}
            </a>
        </li>
        {{/each}}
    </ul>
</li>
</div>
{{/if}}

components/common/social-links:
{{#if social_media}}
    <h5 class="footer-info-heading">Connect with Us:</h5>
    <ul class="socialLinks socialLinks--alt">
        {{#each social_media}}
            <li class="socialLinks-item">
                {{#if display_name '===' 'google+'}}
                    <a class="icon icon--google" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-google" /></svg>
                    </a>
                {{else}}
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank">
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                {{/if}}
            </li>
        {{/each}}
    </ul>
{{/if}}
